
public class Swapping_Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String x = "Water";
		String y = "Milk";
		String z;
		z = x + y;
		
		float b =  (float) 1.2;
		int a = (int) b; 
		
		System.out.println("x: "+ x+"\ny: "+ y+ "\nz: "+ z + "\na: "+ a);

	}

}
